package core.controller;import core.model.Student;import core.service.LoginService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.ResponseBody;import javax.servlet.http.HttpServletRequest;/** * Created by gipin on 2017/3/8. */@Controller@RequestMapping("/login")public class LoginAction {    @Autowired(required = true)    private LoginService loginService;    @RequestMapping(value="/list")    public String list(){        System.out.println("测试");        return "test";    }    @ResponseBody    @RequestMapping(value="/add")    public void add(HttpServletRequest request, @RequestBody Student student){        loginService.insert(student);    }}