package core.model;/** * Created by gipin on 2017/1/7. */public class Product {    /**     * 产品名称     */    private String name;    /**     * 价格     */    private int price;    /**     * 描述     */    private String description;    public Product() {    }    public Product(String name, int price, String description) {        this.name = name;        this.price = price;        this.description = description;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public int getPrice() {        return price;    }    public void setPrice(int price) {        this.price = price;    }    public String getDescription() {        return description;    }    public void setDescription(String description) {        this.description = description;    }}