package core.service;import core.mapper.StudentMapper;import core.model.Student;import framework.service.BaseService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;/** * Created by gipin on 2017/3/8. */@Servicepublic class LoginService implements BaseService<Student> {    @Autowired(required = true)    private StudentMapper studentMapper;    @Override    public int insert(Student record) {        int i=studentMapper.insert(record);        return i;    }}